			group.add( new Text3D( "-z", h1, [2, 2, 5], [0, 20, -distance], [0,              0, 0]) ); 
			group.add( new Text3D( "+y", h1, [2, 2, 5], [0,   distance, 0], [ Math.PI * 0.5, 0, 0]) ); 
			group.add( new Text3D( "-y", h1, [2, 2, 5], [0,  -distance, 0], [-Math.PI * 0.5, 0, 0]) ); 
