female = {};

var textureLoader = new THREE.TextureLoader();
textureLoader.crossOrigin = "anonymous";
var map = textureLoader.load("https://i.imgur.com/GuCUGCU.jpg");
var emissiveMap = textureLoader.load("https://i.imgur.com/VGW8BZ2.jpg");

var url = "/skinned/others/RLCFemaleRiggedBody_v06.json";
var loader = new THREE.XHRLoader();
loader.setResponseType( "json" );
loader.load(url, function( json ){
	debugMode && console.log( json );

	var loader = new THREE.JSONLoader();
	var object = loader.parse( json );
	debugMode && console.log( object );

	var material = new THREE.MeshStandardMaterial({
		name: "FMRLC_BodyMaterial",
		map: map,
		emissiveMap: emissiveMap,
		color: new THREE.Color(1,1,1),
		emissive: new THREE.Color(1,1,1),
		skinning: true, // important!
	});

/*
	var material;
	for (var i = 0; i < object.materials.length; i++ ){
		object.materials[i].skinning = true; // important!
	}

	if ( object.materials.length > 1 ) {
		material = new THREE.MultiMaterial(object.materials);
	} else if ( object.materials.length == 1 ) {
		material = object.materials[0];
	} 
*/

	var geometry = object.geometry;

	geometry.name = json.name;
	geometry.computeFaceNormals();
	geometry.computeVertexNormals();
	geometry.computeBoundingBox();
	geometry.computeBoundingSphere();
	geometry.sourceFile = url;  // important!

	var skinned = new THREE.SkinnedMesh( geometry, material );

	skinned.renderDepth = 1;
	skinned.frustumCulled = false;
	skinned.position.set( 0, 0, 0 );
	skinned.rotation.set( 0, 0, 0 );
	skinned.scale.set( 1, 1, 1 );
	skinned.castShadow = true;
	skinned.name = object.name;

	female.body = skinned;

	localPlayer.outfit.setGender("female");
	localPlayer.outfit.add({"body":female.body});
	localPlayer.outfit.AnimationsHandler.refresh(); // important?

});
